{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code]\nimport numpy as np\n\ndef WindowGet(signal, samplingRate):\n    window = []\n    index = 0\n    windowLen = 8*samplingRate\n    overlapLen = 2*samplingRate\n    while(index<=(len(signal) - windowLen)):\n        win = signal[index: index+windowLen]\n        window.append(win)\n        index = index + overlapLen\n    window = np.squeeze(np.array(window))\n    return window\n\ndef preprocess_according_paper2(subject_no):\n    ecgTrain = []\n    labelTrain = []\n    ecgTest = []\n    labelTest = []\n    for i in tqdm(range(1,16)):\n        pklFilePath = f'../input/ppgdalia/data/PPG_FieldStudy/S{i}/S{i}.pkl'\n        with open(pklFilePath, 'rb') as f:\n            u = pickle._Unpickler(f)\n            u.encoding = 'latin1'\n            data = u.load()\n        label = data['label']\n        ecg = np.array(data['signal']['wrist']['BVP'])\n        ecg_window = WindowGet(ecg, PPG_SAMPLING_RATE)\n\n        if i==(subject_no):\n            labelTest.extend(label)\n            ecgTest.extend(ecg_window)\n        else:\n            labelTrain.extend(label)\n            ecgTrain.extend(ecg_window)\n\n    X_valid, X_test, y_valid, y_test = train_test_split(np.array(ecgTest), np.array(labelTest), test_size=0.5, random_state=42)\n    return (np.array(ecgTrain), np.array(labelTrain)), (X_valid, y_valid), (X_test, y_test)","metadata":{"_uuid":"183502dc-46be-446c-99cb-26d100bdcf61","_cell_guid":"22468f58-a7e5-4284-87c8-28f64d1019f1","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}